mapper.insert:
insert into 
	insta_users (
		insta_id
		, store_no
		, profile_pic_url
	) values(
		#{insta_id}
		, #{store_no}
		, #{profile_pic_url}
	)

insert.foreach:
into insta_users (
	insta_id
	, store_no
	, profile_pic_url
) values (
	#{item.insta_id}
	, #{item.store_no}
	, #{item.profile_pic_url}
)
