mapper.insert:
insert into 
	users (
		user_email
		, user_pw
		, user_name
		, user_type

		, user_profile
		, user_profileId

	) values (
		#{user_email}
		, #{user_pw}
		, #{user_name}
		, #{user_type}

		, #{user_profile}
		, #{user_profileId}

	)

mapper.update:
update
	users
set
	user_pw =#{user_pw}
	, user_name =#{user_name}

	, user_profile = #{user_profile}
	, user_profileId = #{user_profileId}

where
	user_email = #{user_email}

update
	users
set
	user_name = #{user_name}

	, user_profile = #{user_profile}
	, user_profileId = #{user_profileId}

where
	user_email = #{user_email}
