mapper.insert:
insert into 
	insta_locations (
		location_id
		, store_no
		, location_x
		, location_y
	) values(
		#{location_id}
		, #{store_no}
		, #{location_x}
		, #{location_y}
	)

insert.foreach:
into insta_locations (
	location_id
	, store_no
	, location_x
	, location_y
) values (
	#{item.location_id}
	, #{item.store_no}
	, #{item.location_x}
	, #{item.location_y}
)
