mapper.insert:
insert into 
	mango_days (
		store_no
		, mango_sun
		, mango_mon
		, mango_tue
		, mango_wed
		, mango_thu
		, mango_fri
		, mango_sat
		, mango_indate
	) values(
		#{store_no}
		, #{mango_sun}
		, #{mango_mon}
		, #{mango_tue}
		, #{mango_wed}
		, #{mango_thu}
		, #{mango_fri}
		, #{mango_sat}
		, to_date(#{mango_indate}, 'YYYY-MM-DD HH24:MI:SS')
	)
