mapper.insert:
insert into 
	mango_stores (
		store_no
		, mango_tel
		, mango_kind
		, mango_price
		, mango_parking
		, mango_time
		, mango_break_time
		, mango_last_order
		, mango_holiday
	) values(
		#{store_no}
		, #{mango_tel}
		, #{mango_kind}
		, #{mango_price}
		, #{mango_parking}
		, #{mango_time}
		, #{mango_break_time}
		, #{mango_last_order}
		, #{mango_holiday}
	)

mapper.select:
select m.store_no , m.mango_tel , m.mango_kind , m.mango_price , m.mango_parking , m.mango_time , m.mango_break_time , m.mango_last_order , m.mango_holiday , m.mango_indate , d.mango_sun , d.mango_mon , d.mango_tue , d.mango_wed , d.mango_thu , d.mango_fri , d.mango_sat from mango_stores m, mango_days d where m.store_no = d.store_no
and d.mango_sun = #{mango_sun}
and d.mango_mon = #{mango_mon}
and d.mango_tue = #{mango_tue}
and d.mango_wed = #{mango_wed}
and d.mango_thu = #{mango_thu}
and d.mango_fri = #{mango_fri}
and d.mango_sat = #{mango_sat}

mapper.update:
update
	mango_stores
set
	mango_tel = #{mango_holiday}
	, mango_kind = #{mango_holiday}
	, mango_price = #{mango_holiday}
	, mango_parking = #{mango_holiday}
	, mango_time = #{mango_holiday}
	, mango_break_time = #{mango_holiday}
	, mango_last_order = #{mango_holiday}
	, mango_holiday = #{mango_holiday}
	, mango_indate = sysdate
where
	store_no = #{store_no}
