mapper.insert:
insert into 
	users(
		user_id
		, user_pw
		, user_name
		, user_email
	)values(
		# {user_id}
		, #{user_pw}
		, #{user_name}
		, #{user_email}
	)

mapper.update:
update
	users
set
	user_pw = #{user_pw}
	, user_name = #{user_name}
	, user_email = #{user_email}
where
	user_id = #{user_id}
